  const [isOpenContact, setIsOpenContact] = useState(false);
  const [successBox, setSuccessBox] = useState(false);
  const [receiptData, setReceiptData] = useState([]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleReceiptClick = () => {
    toggleDropdown();
    fetchReceiptData();
  };

  const toggleDropdownSupport = () => {
    setIsOpenSupport(!isOpenSupport);
  };

  const toggleDropdownContact = () => {
    setIsOpenContact(!isOpenContact);
  };

  const toggleSuccessBox = (e) => {
    e.preventDefault();

    setSuccessBox(!successBox);

    document.querySelectorAll(".support-block.show").forEach((element) => {
      element.classList.remove("show");
    });

    document.querySelectorAll(".contact-block.show").forEach((element) => {
      element.classList.remove("show");
    });

    document.querySelectorAll(".success-block").forEach((element) => {
      element.classList.add("show");
    });
  };

  const closeModal = () => {
    document.querySelectorAll(".success-block.show").forEach((element) => {
      element.classList.remove("show");
    });
    // setSuccessBox(false);
  };

  const fetchReceiptData = async () => {
    try {
      const response = await axios.post(
        "receipt/list",
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setReceiptData(response.data.data);
    } catch (error) {
      console.error("Error Fetching Receipt Data", error);
    }
  };
  // console.log("receiptData", receiptData);

